  cuseContext(CartContext);
  return (
    <button className='button' onClick={props.onClick}>
        <span className='icon'><CartIcon /> </span>
        <span>Your Cart</span>
        <span className='badge'>no_of_items</span>


    </button>
  )
}

export default HeaderCartButton
