import React from "react";
import "./MealItemForm.css";
import Input from "../../UI/Input";

const MealItemForm = () => {
  return (
    <form className="form">
      <Input 
      label="quantity" 
      input={
        {
            id:'amount',
            type:'number',
            min:'1',
            max:'5',
            defaultValue:'1',

        }
        } />
      <button type="submit">+Add</button>
    </form>
  );
};

export default MealItemForm;
