        <h2>{props.name}</h2>
        <div className='summary'>
          <div className='price'>{price}</div>
          <spa className='amount'> {props.amount}</spa>
        </div>
      </div>
      <div className='actions'>
        <button onClick={props.onRemove}>âˆ’</button>
        <button onClick={props.onAdd}>+</button>
      </div>
    </li>
  );
};

